import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Login() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  
  // Show loading if auth is not ready
  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }
  
  const { login } = auth;

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.email) errs.email = 'Email is required';
    if (!form.password) errs.password = 'Password is required';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      const res = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      login(data.data.token, data.data.user);
      toast.success('Login successful!');
      if (data.data.user.role === 'admin') navigate('/admin');
      else navigate('/dashboard');
    } catch (err) {
      toast.error(err.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute top-0 left-0 w-72 h-72 bg-primary-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
      <div className="absolute top-0 right-0 w-72 h-72 bg-purple-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
      <div className="absolute -bottom-8 left-20 w-72 h-72 bg-pink-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>
      {/* Floating geometric shapes */}
      <div className="absolute top-20 right-20 w-16 h-16 bg-primary-100 rounded-lg rotate-12 opacity-60"></div>
      <div className="absolute bottom-20 left-20 w-12 h-12 bg-purple-100 rounded-full opacity-60"></div>
      <div className="absolute top-1/2 left-10 w-8 h-8 bg-pink-100 rounded-lg rotate-45 opacity-60"></div>
      <div className="flex flex-col items-center justify-center min-h-screen relative z-10">
        {/* Capital Rise Logo */}
        <div className="mb-4 text-center">
          <div className="text-5xl font-bold bg-gradient-to-r from-primary-600 to-purple-600 bg-clip-text text-transparent mb-2">
            Capital Rise
          </div>
        </div>
        {/* Caption */}
        <div className="mb-8 text-center">
          <div className="text-2xl md:text-3xl font-semibold text-gray-800 tracking-tight drop-shadow-sm">
            A Smart Way to Earn
          </div>
        </div>
        {/* Login Card */}
        <form onSubmit={handleSubmit} className="w-full max-w-md bg-white/90 backdrop-blur-sm p-8 rounded-2xl shadow-2xl border border-white/30 flex flex-col gap-4">
          <Input
            label="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            error={errors.email}
            autoComplete="email"
          />
          <Input
            label="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            error={errors.password}
            autoComplete="current-password"
          />
          <Button type="submit" loading={loading} className="w-full mt-2 bg-gradient-to-r from-primary-600 to-purple-600 hover:from-primary-700 hover:to-purple-700 text-white font-semibold py-3 rounded-xl transition-all duration-300 transform hover:scale-105">
            Sign In
          </Button>
          <div className="text-center mt-2 text-sm text-gray-600">
            Don&apos;t have an account? <a href="/register" className="text-primary-600 hover:text-primary-700 font-semibold transition-colors">Register</a>
          </div>
        </form>
      </div>
    </div>
  );
}
