import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Register() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [registrationDetails, setRegistrationDetails] = useState(null);

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.username) errs.username = 'Username is required';
    if (!form.email) errs.email = 'Email is required';
    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'Password must be at least 6 characters';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      const res = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      setRegistrationDetails(data.data.user);
      toast.success('Registration successful!');
    } catch (err) {
      toast.error(err.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      {registrationDetails ? (
        <div className="w-full max-w-md bg-white p-8 rounded shadow">
          <h1 className="text-3xl font-bold mb-6 text-center text-green-600">Registration Successful!</h1>
          <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
            <h2 className="text-lg font-semibold mb-4 text-green-800">Your Account Details:</h2>
            <div className="space-y-3">
              <div>
                <span className="font-medium text-gray-700">Username:</span>
                <span className="ml-2 font-mono bg-gray-100 px-2 py-1 rounded">{registrationDetails.username}</span>
              </div>
              <div>
                <span className="font-medium text-gray-700">Email:</span>
                <span className="ml-2 font-mono bg-gray-100 px-2 py-1 rounded">{registrationDetails.email}</span>
              </div>
              <div>
                <span className="font-medium text-gray-700">Password:</span>
                <span className="ml-2 font-mono bg-gray-100 px-2 py-1 rounded">{registrationDetails.password}</span>
              </div>
            </div>
          </div>
          <div className="text-center space-y-3">
            <Button 
              onClick={() => navigate('/login')} 
              className="w-full"
              variant="primary"
            >
              Go to Login
            </Button>
            <Button 
              onClick={() => {
                setRegistrationDetails(null);
                setForm({ username: '', email: '', password: '' });
              }} 
              className="w-full"
              variant="secondary"
            >
              Register Another Account
            </Button>
          </div>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="w-full max-w-sm bg-white p-8 rounded shadow">
          <h1 className="text-3xl font-bold mb-6 text-center">Register</h1>
          <Input
            label="Username"
            name="username"
            value={form.username}
            onChange={handleChange}
            error={errors.username}
            autoComplete="username"
          />
          <Input
            label="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            error={errors.email}
            autoComplete="email"
          />
          <Input
            label="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            error={errors.password}
            autoComplete="new-password"
          />
          <Button type="submit" loading={loading} className="w-full mt-2">Register</Button>
          <div className="text-center mt-4 text-sm">
            Already have an account? <a href="/login" className="text-primary-600 hover:underline">Login</a>
          </div>
        </form>
      )}
    </div>
  );
}
