import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import BalanceCard from '../components/BalanceCard';
import LootCard from '../components/LootCard';
import ReferralCard from '../components/ReferralCard';
import LootForm from '../components/LootForm';
import WithdrawalSetup from './WithdrawalSetup';

const ClientDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [balance, setBalance] = useState({ balance: 0, total_earned: 0 });
  const [loots, setLoots] = useState([]);
  const [referrals, setReferrals] = useState([]);
  const [withdrawals, setWithdrawals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showLootForm, setShowLootForm] = useState(false);
  const [selectedLoot, setSelectedLoot] = useState(null);

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    fetchData();
  }, [user, navigate]);

  const fetchData = async () => {
    try {
      const [balanceRes, lootsRes, referralsRes, withdrawalsRes] = await Promise.all([
        fetch('/api/client/balance', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/client/loots', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/client/referrals', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/client/withdrawals', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      const [balanceData, lootsData, referralsData, withdrawalsData] = await Promise.all([
        balanceRes.json(),
        lootsRes.json(),
        referralsRes.json(),
        withdrawalsRes.json()
      ]);

      if (balanceData.success) setBalance(balanceData.data);
      if (lootsData.success) setLoots(lootsData.data);
      if (referralsData.success) setReferrals(referralsData.data);
      if (withdrawalsData.success) setWithdrawals(withdrawalsData.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInvest = (loot) => {
    setSelectedLoot(loot);
    setShowLootForm(true);
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Capital Rise</h1>
              <p className="text-sm text-gray-600">Welcome back, {user?.username}</p>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={handleLogout}
                className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <nav className="flex space-x-8">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'dashboard'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveTab('withdrawals')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'withdrawals'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Withdrawals
            </button>
            <button
              onClick={() => setActiveTab('setup')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'setup'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Setup Withdrawal
            </button>
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeTab === 'dashboard' && (
          <>
            <BalanceCard balance={balance.balance} totalEarned={balance.total_earned} />
            {loots && loots.length > 0 ? (
              loots.map(loot => loot && loot.is_active && (
                <LootCard key={loot.id} loot={loot} onInvest={handleInvest} />
              ))
            ) : (
              <div className="text-center text-gray-500 py-8">
                No active offers available at the moment.
              </div>
            )}
            <ReferralCard referrals={referrals} />
          </>
        )}
        {activeTab === 'withdrawals' && (
          <div className="grid gap-4">
            {withdrawals.map(withdrawal => (
              <div key={withdrawal.id} className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">
                      ₹{withdrawal.amount}
                    </h3>
                    <p className="text-sm text-gray-600">
                      UPI: {withdrawal.upi_id}
                    </p>
                    <p className="text-sm text-gray-500">
                      {new Date(withdrawal.created_at).toLocaleDateString()}
                    </p>
                  </div>
                  <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                    withdrawal.status === 'approved' ? 'bg-green-100 text-green-800' :
                    withdrawal.status === 'rejected' ? 'bg-red-100 text-red-800' :
                    'bg-yellow-100 text-yellow-800'
                  }`}>
                    {withdrawal.status}
                  </span>
                </div>
                {withdrawal.notes && (
                  <p className="mt-2 text-sm text-gray-600">
                    Note: {withdrawal.notes}
                  </p>
                )}
                {withdrawal.reference_number && (
                  <p className="mt-1 text-sm text-gray-600">
                    Reference: {withdrawal.reference_number}
                  </p>
                )}
              </div>
            ))}
            {withdrawals.length === 0 && (
              <div className="text-center text-gray-500 py-8">
                No withdrawal requests yet.
              </div>
            )}
          </div>
        )}
        {activeTab === 'setup' && (
          <WithdrawalSetup />
        )}
      </div>

      {/* Loot Form Modal */}
      {showLootForm && selectedLoot && (
        <LootForm
          loot={selectedLoot}
          onClose={() => {
            setShowLootForm(false);
            setSelectedLoot(null);
          }}
          onSuccess={() => {
            setShowLootForm(false);
            setSelectedLoot(null);
            fetchData();
          }}
        />
      )}
    </div>
  );
};

export default ClientDashboard;
