import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Register() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', email: '', mobile: '', password: '', withdrawal_password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.username) errs.username = 'Username is required';
    if (!form.email) errs.email = 'Email is required';
    else if (!form.email.toLowerCase().endsWith('@gmail.com')) {
      errs.email = 'Only Gmail addresses (@gmail.com) are allowed';
    }
    if (!form.mobile) errs.mobile = 'Mobile number is required';
    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'Password must be at least 6 characters';
    if (!form.withdrawal_password) errs.withdrawal_password = 'Withdrawal password is required';
    else if (form.withdrawal_password.length < 4) errs.withdrawal_password = 'Withdrawal password must be at least 4 characters';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      const res = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Registration successful! You can now log in.');
      navigate('/login');
    } catch (err) {
      toast.error(err.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form onSubmit={handleSubmit} className="w-full max-w-sm bg-white p-8 rounded shadow">
        <h1 className="text-3xl font-bold mb-6 text-center">Register</h1>
        <Input
          label="Username"
          name="username"
          value={form.username}
          onChange={handleChange}
          error={errors.username}
          autoComplete="username"
        />
        <Input
          label="Email"
          name="email"
          type="email"
          value={form.email}
          onChange={handleChange}
          error={errors.email}
          autoComplete="email"
        />
        <Input
          label="Mobile Number"
          name="mobile"
          type="tel"
          value={form.mobile}
          onChange={handleChange}
          error={errors.mobile}
          autoComplete="tel"
          placeholder="Enter your mobile number"
        />
        <Input
          label="Password"
          name="password"
          type="password"
          value={form.password}
          onChange={handleChange}
          error={errors.password}
          autoComplete="new-password"
        />
        <Input
          label="Withdrawal Password"
          name="withdrawal_password"
          type="password"
          value={form.withdrawal_password}
          onChange={handleChange}
          error={errors.withdrawal_password}
          autoComplete="new-password"
          placeholder="Enter withdrawal password (min 4 characters)"
        />
        <Button type="submit" loading={loading} className="w-full mt-2">Register</Button>
        <div className="text-center mt-4 text-sm">
          Already have an account? <a href="/login" className="text-primary-600 hover:underline">Login</a>
        </div>
      </form>
    </div>
  );
}
