import React from 'react';

export default function Button({
  children,
  type = 'button',
  variant = 'primary',
  loading = false,
  className = '',
  ...props
}) {
  return (
    <button
      type={type}
      className={`btn btn-${variant} ${className}`}
      disabled={loading || props.disabled}
      {...props}
    >
      {loading ? (
        <span className="animate-spin mr-2">‚è≥</span>
      ) : null}
      {children}
    </button>
  );
}
