import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import LootCard from '../components/LootCard';
import BalanceCard from '../components/BalanceCard';
import ReferralCard from '../components/ReferralCard';
import Input from '../components/Input';
import Button from '../components/Button';
import toast from 'react-hot-toast';
import ChatBox from '../components/ChatBox';
import moment from 'moment-timezone';

export default function ClientDashboard() {
  const navigate = useNavigate();
  const auth = useAuth();
  const [loots, setLoots] = useState([]);
  const [balance, setBalance] = useState({ balance: 0, total_earned: 0 });
  const [investments, setInvestments] = useState([]);
  const [referrals, setReferrals] = useState([]);
  const [withdrawals, setWithdrawals] = useState([]);
  const [showInvestModal, setShowInvestModal] = useState(false);
  const [showWithdrawModal, setShowWithdrawModal] = useState(false);
  const [selectedLoot, setSelectedLoot] = useState(null);
  const [investAmount, setInvestAmount] = useState('');
  const [customerAmount, setCustomerAmount] = useState('');
  const [earnAmount, setEarnAmount] = useState('');
  const [investError, setInvestError] = useState('');
  const [withdrawAmount, setWithdrawAmount] = useState('');
  const [withdrawPassword, setWithdrawPassword] = useState('');
  const [withdrawError, setWithdrawError] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  
  // Profile state
  const [changeWithdrawalPassword, setChangeWithdrawalPassword] = useState(false);
  const [changeUpiId, setChangeUpiId] = useState(false);
  const [withdrawalForm, setWithdrawalForm] = useState({ current_withdrawal_password: '', new_withdrawal_password: '' });
  const [upiForm, setUpiForm] = useState({ account_password: '', new_upi_id: '' });
  const [withdrawalErrors, setWithdrawalErrors] = useState({});
  const [upiErrors, setUpiErrors] = useState({});

  const TABS = [
    { key: 'dashboard', label: 'Dashboard' },
    { key: 'withdrawals', label: 'Withdrawal History' },
    { key: 'custom-offers', label: 'Custom Offers' },
    { key: 'referrals', label: 'Referral History' },
    { key: 'profile', label: 'Profile' }
  ];

  useEffect(() => {
    fetchLoots();
    fetchBalance();
    fetchInvestments();
    fetchReferrals();
    fetchWithdrawals();
  }, []);

  const fetchLoots = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/loots', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Loots fetched:', data.data);
      setLoots(data.data);
    } else {
      console.error('Failed to fetch loots:', data.message);
    }
  };
  const fetchBalance = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/balance', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setBalance(data.data);
  };
  const fetchInvestments = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/investments', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Investments fetched:', data.data);
      setInvestments(data.data);
    } else {
      console.error('Failed to fetch investments:', data.message);
    }
  };
  const fetchReferrals = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/referrals', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setReferrals(data.data);
  };

  const fetchWithdrawals = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/withdrawals', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setWithdrawals(data.data);
  };

  useEffect(() => {
    if (token) {
      fetchLoots();
      fetchBalance();
      fetchInvestments();
      fetchReferrals();
      fetchWithdrawals();
      fetchLeaderboardData();
      fetchLeaderboardHistory();
    }
  }, [token]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchLeaderboardData = async () => {
    try {
      const res = await fetch('/api/client/leaderboard/position', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      const data = await res.json();
      
      if (data.success && data.data.hasActiveSeason) {
        const { clientEntry, topEntries, totalParticipants, season } = data.data;
        
        // Update current rank
        document.getElementById('currentRank').textContent = clientEntry ? clientEntry.rank : '-';
        
        // Update season earnings
        document.getElementById('seasonEarnings').textContent = clientEntry ? `₹${clientEntry.total_earned}` : '₹0.00';
        
        // Update total participants
        document.getElementById('totalParticipants').textContent = totalParticipants;
        
        // Update season info
        document.getElementById('seasonName').textContent = season.name;
        document.getElementById('seasonDuration').textContent = `${moment(season.start_date).format('DD/MM/YYYY')} - ${moment(season.end_date).format('DD/MM/YYYY')}`;
        document.getElementById('bonusAmount').textContent = `₹${season.bonus_amount}`;
        
        // Update leaderboard table
        const leaderboardTable = document.getElementById('leaderboardTable');
        leaderboardTable.innerHTML = '';
        
        topEntries.forEach((entry, index) => {
          const row = document.createElement('tr');
          row.className = 'border-t';
          row.innerHTML = `
            <td className="px-4 py-2">${entry.rank}</td>
            <td className="px-4 py-2">${entry.username}</td>
            <td className="px-4 py-2 font-medium">₹${entry.total_earned}</td>
          `;
          leaderboardTable.appendChild(row);
        });
      } else {
        // No active season
        document.getElementById('currentRank').textContent = '-';
        document.getElementById('seasonEarnings').textContent = '₹0.00';
        document.getElementById('totalParticipants').textContent = '-';
        document.getElementById('seasonName').textContent = 'No Active Season';
        document.getElementById('seasonDuration').textContent = 'No season currently running';
        document.getElementById('bonusAmount').textContent = '₹0';
        
        const leaderboardTable = document.getElementById('leaderboardTable');
        leaderboardTable.innerHTML = '<tr><td colSpan="3" className="text-center py-4 text-gray-500">No active season</td></tr>';
      }
    } catch (error) {
      console.error('Error fetching leaderboard data:', error);
    }
  };

  const fetchLeaderboardHistory = async () => {
    try {
      const res = await fetch('/api/client/leaderboard/history', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      const data = await res.json();
      
      if (data.success) {
        const historyTable = document.getElementById('leaderboardHistoryTable');
        historyTable.innerHTML = '';
        
        if (data.data.length === 0) {
          historyTable.innerHTML = '<tr><td colSpan="5" className="text-center py-4 text-gray-500">No history available</td></tr>';
          return;
        }
        
        data.data.forEach((season) => {
          const row = document.createElement('tr');
          row.className = 'border-t';
          
          const status = season.won_bonus ? 'Winner' : 'Participated';
          const rankText = season.rank ? `#${season.rank}` : 'N/A';
          const earningsText = season.total_earned ? `₹${season.total_earned}` : '₹0';
          const bonusText = season.won_bonus ? `₹${season.won_bonus}` : '-';
          
          row.innerHTML = `
            <td className="px-4 py-2">${season.name}</td>
            <td className="px-4 py-2">${rankText}</td>
            <td className="px-4 py-2">${earningsText}</td>
            <td className="px-4 py-2">${bonusText}</td>
            <td className="px-4 py-2">
              <span className="badge ${season.won_bonus ? 'badge-success' : 'badge-secondary'}">${status}</span>
            </td>
          `;
          historyTable.appendChild(row);
        });
      }
    } catch (error) {
      console.error('Error fetching leaderboard history:', error);
    }
  };


  const handleInvest = loot => {
    console.log('Investing in loot:', loot);
    setSelectedLoot(loot);
    setInvestAmount('');
    setCustomerAmount('');
    setEarnAmount('');
    setInvestError('');
    setShowInvestModal(true);
  };

  const submitInvest = async e => {
    e.preventDefault();
    setInvestError('');
    
    // Validation
    if (!customerAmount || isNaN(customerAmount) || Number(customerAmount) <= 0) {
      setInvestError('Enter a valid customer amount');
      return;
    }
    
    if (!earnAmount || isNaN(earnAmount) || Number(earnAmount) <= 0) {
      setInvestError('Enter a valid earn amount');
      return;
    }
    
    const totalAmount = Number(customerAmount) + Number(earnAmount);
    
    if (totalAmount > selectedLoot.max_amount) {
      setInvestError(`Total amount (₹${totalAmount}) cannot exceed ₹${selectedLoot.max_amount}`);
      return;
    }
    
    if (Number(earnAmount) >= Number(customerAmount)) {
      setInvestError('Earn amount should be less than customer amount');
      return;
    }
    
    console.log('Submitting custom investment:', { 
      loot_id: selectedLoot.id, 
      customer_amount: customerAmount,
      earn_amount: earnAmount,
      total_amount: totalAmount
    });
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const requestBody = { 
        loot_id: selectedLoot.id, 
        customer_amount: Number(customerAmount),
        earn_amount: Number(earnAmount),
        total_amount: totalAmount
      };
      
      console.log('Sending request to /api/client/invest with body:', requestBody);
      console.log('Token:', token ? 'Present' : 'Missing');
      
      const res = await fetch('/api/client/invest', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody),
      });
      
      console.log('Response status:', res.status);
      console.log('Response headers:', res.headers);
      
      const data = await res.json();
      console.log('Investment response:', data);
      if (!data.success) throw new Error(data.message);
      toast.success('Custom offer created successfully!');
      setShowInvestModal(false);
      fetchInvestments();
      fetchBalance();
    } catch (err) {
      console.error('Investment error:', err);
      setInvestError(err.message || 'Investment failed');
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = () => {
    setWithdrawAmount('');
    setWithdrawPassword('');
    setWithdrawError('');
    setShowWithdrawModal(true);
  };

  // Profile functions
  const handleWithdrawalPasswordChange = async (e) => {
    e.preventDefault();
    const errs = {};
    if (!withdrawalForm.current_withdrawal_password) errs.current_withdrawal_password = 'Current password is required';
    if (!withdrawalForm.new_withdrawal_password) errs.new_withdrawal_password = 'New password is required';
    else if (withdrawalForm.new_withdrawal_password.length < 4) errs.new_withdrawal_password = 'New password must be at least 4 characters';
    
    if (Object.keys(errs).length) {
      setWithdrawalErrors(errs);
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/auth/change-withdrawal-password', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(withdrawalForm),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Withdrawal password changed successfully!');
      setChangeWithdrawalPassword(false);
      setWithdrawalForm({ current_withdrawal_password: '', new_withdrawal_password: '' });
      setWithdrawalErrors({});
    } catch (err) {
      toast.error(err.message || 'Failed to change withdrawal password');
    } finally {
      setLoading(false);
    }
  };

  const handleUpiIdChange = async (e) => {
    e.preventDefault();
    const errs = {};
    if (!upiForm.account_password) errs.account_password = 'Account password is required';
    if (!upiForm.new_upi_id) errs.new_upi_id = 'New UPI ID is required';
    else if (upiForm.new_upi_id.length < 3) errs.new_upi_id = 'UPI ID must be at least 3 characters';
    
    if (Object.keys(errs).length) {
      setUpiErrors(errs);
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/auth/change-upi', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(upiForm),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('UPI ID changed successfully!');
      setChangeUpiId(false);
      setUpiForm({ account_password: '', new_upi_id: '' });
      setUpiErrors({});
      // Update user data in auth context
      auth.updateUser({ upi_id: upiForm.new_upi_id });
    } catch (err) {
      toast.error(err.message || 'Failed to change UPI ID');
    } finally {
      setLoading(false);
    }
  };

  const submitWithdraw = async (e) => {
    e.preventDefault();
    setWithdrawError('');
    
    if (!withdrawAmount || isNaN(withdrawAmount) || Number(withdrawAmount) <= 0) {
      setWithdrawError('Enter a valid amount');
      return;
    }
    
    if (Number(withdrawAmount) > balance.balance) {
      setWithdrawError('Cannot withdraw more than balance');
      return;
    }
    
    if (!withdrawPassword || withdrawPassword.trim() === '') {
      setWithdrawError('Withdrawal password is required');
      return;
    }
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/client/withdraw', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ 
          amount: withdrawAmount, 
          withdrawal_password: withdrawPassword.trim() 
        }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Withdrawal request submitted!');
      setShowWithdrawModal(false);
      fetchBalance();
      fetchWithdrawals();
    } catch (err) {
      setWithdrawError(err.message || 'Withdrawal failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="max-w-3xl mx-auto py-8 px-2">
        {/* Tab Navigation */}
        <div className="flex gap-2 mb-6 border-b">
          {TABS.map(tab => (
            <button
              key={tab.key}
              className={`px-4 py-2 font-medium border-b-2 transition-colors duration-150 ${activeTab === tab.key ? 'border-primary-600 text-primary-700' : 'border-transparent text-gray-500 hover:text-primary-600'}`}
              onClick={() => setActiveTab(tab.key)}
            >
              {tab.label}
            </button>
          ))}
        </div>
        {/* Content based on active tab */}
        {activeTab === 'dashboard' && (
          <>
            <BalanceCard balance={balance} />
            <LootCard loots={loots} onInvest={handleInvest} />
            <ReferralCard referrals={referrals} />
          </>
        )}
        {activeTab === 'withdrawals' && (
          <div className="grid gap-4">
            {withdrawals.map(withdrawal => (
              <div key={withdrawal.id} className="bg-white p-4 rounded-lg shadow-md">
                <p><strong>Amount:</strong> ₹{withdrawal.amount}</p>
                <p><strong>Status:</strong> {withdrawal.status}</p>
                <p><strong>Date:</strong> {moment(withdrawal.created_at).format('DD/MM/YYYY HH:mm')}</p>
                {withdrawal.status === 'completed' && (
                  <p><strong>Completed at:</strong> {moment(withdrawal.completed_at).format('DD/MM/YYYY HH:mm')}</p>
                )}
                {withdrawal.status === 'failed' && (
                  <p><strong>Failed at:</strong> {moment(withdrawal.failed_at).format('DD/MM/YYYY HH:mm')}</p>
                )}
              </div>
            ))}
          </div>
        )}
        {activeTab === 'custom-offers' && (
          <div className="grid gap-4">
            {loots.map(loot => (
              <LootCard key={loot.id} loot={loot} onInvest={handleInvest} />
            ))}
          </div>
        )}
        {activeTab === 'referrals' && (
          <div className="grid gap-4">
            {referrals.map(referral => (
              <ReferralCard key={referral.id} referral={referral} />
            ))}
          </div>
        )}
        {activeTab === 'profile' && (
          <div className="grid gap-4">
            <h2 className="text-2xl font-bold">Profile</h2>
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h3 className="text-xl font-semibold mb-4">Withdrawal Password</h3>
              <p>Current UPI ID: {auth.user.upi_id || 'N/A'}</p>
              <Button variant="secondary" onClick={() => setChangeUpiId(true)} className="mt-4">Change UPI ID</Button>
              {changeUpiId && (
                <div className="mt-4">
                  <h4 className="text-lg font-semibold mb-2">Change UPI ID</h4>
                  <form onSubmit={handleUpiIdChange} className="grid gap-3">
                    <Input
                      type="password"
                      label="Current UPI ID Password"
                      value={upiForm.account_password}
                      onChange={(e) => setUpiForm({ ...upiForm, account_password: e.target.value })}
                      error={upiErrors.account_password}
                      required
                    />
                    <Input
                      type="text"
                      label="New UPI ID"
                      value={upiForm.new_upi_id}
                      onChange={(e) => setUpiForm({ ...upiForm, new_upi_id: e.target.value })}
                      error={upiErrors.new_upi_id}
                      required
                    />
                    <Button type="submit" loading={loading} className="mt-4">Change UPI ID</Button>
                  </form>
                </div>
              )}

              <h3 className="text-xl font-semibold mt-6 mb-4">Withdrawal Password</h3>
              <p>Current Withdrawal Password: {auth.user.withdrawal_password ? 'Set' : 'Not Set'}</p>
              <Button variant="secondary" onClick={() => setChangeWithdrawalPassword(true)} className="mt-4">Change Withdrawal Password</Button>
              {changeWithdrawalPassword && (
                <div className="mt-4">
                  <h4 className="text-lg font-semibold mb-2">Change Withdrawal Password</h4>
                  <form onSubmit={handleWithdrawalPasswordChange} className="grid gap-3">
                    <Input
                      type="password"
                      label="Current Withdrawal Password"
                      value={withdrawalForm.current_withdrawal_password}
                      onChange={(e) => setWithdrawalForm({ ...withdrawalForm, current_withdrawal_password: e.target.value })}
                      error={withdrawalErrors.current_withdrawal_password}
                      required
                    />
                    <Input
                      type="password"
                      label="New Withdrawal Password"
                      value={withdrawalForm.new_withdrawal_password}
                      onChange={(e) => setWithdrawalForm({ ...withdrawalForm, new_withdrawal_password: e.target.value })}
                      error={withdrawalErrors.new_withdrawal_password}
                      required
                    />
                    <Button type="submit" loading={loading} className="mt-4">Change Withdrawal Password</Button>
                  </form>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
      {/* Chat with Admin */}
      <div className="mt-12">
        <ChatBox />
      </div>
    </div>
  );
}
