import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Login() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.email) errs.email = 'Email is required';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      toast.success('Login successful!');
      navigate('/dashboard');
    } catch (err) {
      toast.error('Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center bg-gradient-to-br from-pink-200 via-yellow-100 to-blue-200 relative overflow-hidden">
      {/* Decorative background shapes (optional, for extra style) */}
      <div className="absolute top-0 left-0 w-96 h-96 bg-pink-300 rounded-full opacity-30 blur-2xl pointer-events-none" style={{zIndex:0}}></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-blue-300 rounded-full opacity-30 blur-2xl pointer-events-none" style={{zIndex:0}}></div>
      <div className="absolute top-1/2 left-1/2 w-1/2 h-1/2 bg-yellow-100 rounded-full opacity-20 blur-3xl pointer-events-none transform -translate-x-1/2 -translate-y-1/2" style={{zIndex:0}}></div>
      {/* Caption */}
      <div className="z-10 mb-8 mt-8 text-center">
        <h1 className="text-4xl md:text-5xl font-bold text-gray-900 drop-shadow-lg">A Smart Way to Earn</h1>
      </div>
      {/* Login Card */}
      <div className="z-10 w-full max-w-md mx-auto bg-white/90 rounded-2xl shadow-2xl p-8 border border-white/30 flex flex-col gap-4 backdrop-blur-sm">
        <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">Welcome back</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">Email address</label>
            <input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={form.email}
              onChange={handleChange}
              className="block w-full rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 text-gray-900 text-base"
              placeholder="Email address"
            />
            {errors.email && <div className="text-xs text-red-500 mt-1">{errors.email}</div>}
          </div>
          <Button
            type="submit"
            loading={loading}
            className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-2.5 rounded-md text-base shadow-sm transition-colors"
          >
            Continue
          </Button>
        </form>
        <div className="text-center text-sm text-gray-600 mt-4">
          Don't have an account?{' '}
          <a href="/register" className="text-primary-600 hover:underline font-medium">Sign up</a>
        </div>
      </div>
    </div>
  );
}
