import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Register() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', email: '', mobile: '', password: '', otp: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otpLoading, setOtpLoading] = useState(false);
  const [step, setStep] = useState(1); // 1: basic info, 2: OTP verification
  const [verificationMethod, setVerificationMethod] = useState('whatsapp'); // Default to WhatsApp

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.username) errs.username = 'Username is required';
    
    // Email validation
    if (!form.email) errs.email = 'Email is required';
    else {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(form.email)) {
        errs.email = 'Please enter a valid email address';
      }
    }
    
    // Mobile validation
    if (!form.mobile) errs.mobile = 'Mobile number is required';
    else if (!/^[0-9]{10}$/.test(form.mobile)) {
      errs.mobile = 'Mobile number must be exactly 10 digits';
    }
    
    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'Password must be at least 6 characters';
    
    return errs;
  };

  const validateOTP = () => {
    const errs = {};
    if (!form.otp) errs.otp = 'OTP is required';
    else if (!/^[0-9]{6}$/.test(form.otp)) {
      errs.otp = 'OTP must be 6 digits';
    }
    return errs;
  };

  const handleSendOTP = async () => {
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    
    setOtpLoading(true);
    try {
      const res = await fetch('/api/auth/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          mobile: form.mobile,
          method: verificationMethod 
        }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success(`OTP sent successfully via ${verificationMethod === 'whatsapp' ? 'WhatsApp' : 'SMS'}!`);
      setOtpSent(true);
      setStep(2);
    } catch (err) {
      toast.error(err.message || 'Failed to send OTP');
    } finally {
      setOtpLoading(false);
    }
  };

  const handleVerifyOTP = async () => {
    const errs = validateOTP();
    if (Object.keys(errs).length) return setErrors(errs);
    
    setOtpLoading(true);
    try {
      const res = await fetch('/api/auth/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ mobile: form.mobile, otp: form.otp }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('OTP verified successfully!');
      setStep(3); // Proceed to registration
    } catch (err) {
      toast.error(err.message || 'Failed to verify OTP');
    } finally {
      setOtpLoading(false);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      const res = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Registration successful! You can now log in.');
      navigate('/login');
    } catch (err) {
      toast.error(err.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      <div className="w-full max-w-md mx-auto bg-white/90 rounded-2xl shadow-2xl p-8 border border-white/30 flex flex-col gap-4 backdrop-blur-sm">
        {/* Logo */}
        <div className="mb-4 text-center">
          <span className="text-5xl font-extrabold bg-gradient-to-r from-primary-600 to-pink-500 bg-clip-text text-transparent drop-shadow-lg">Capital Rise</span>
        </div>
        {/* Welcome message */}
        <div className="mb-2 text-center">
          <span className="text-lg text-gray-700 font-medium">Create your account</span>
          <p className="text-sm text-gray-600 mt-1">Enter your details to get started</p>
        </div>
        
        {step === 1 && (
          <div className="flex flex-col gap-4">
            <Input
              label="Username"
              name="username"
              value={form.username}
              onChange={handleChange}
              error={errors.username}
              autoComplete="username"
              className="rounded-lg border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-primary-400 focus:border-primary-400"
            />
            <Input
              label="Email"
              name="email"
              type="email"
              value={form.email}
              onChange={handleChange}
              error={errors.email}
              autoComplete="email"
              placeholder="yourname@example.com"
              className="rounded-lg border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-primary-400 focus:border-primary-400"
            />
            <Input
              label="Mobile Number"
              name="mobile"
              type="tel"
              value={form.mobile}
              onChange={handleChange}
              error={errors.mobile}
              autoComplete="tel"
              placeholder="Enter 10 digit mobile number"
              maxLength="10"
              pattern="[0-9]*"
              inputMode="numeric"
              className="rounded-lg border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-primary-400 focus:border-primary-400"
            />
            <Input
              label="Password"
              name="password"
              type="password"
              value={form.password}
              onChange={handleChange}
              error={errors.password}
              autoComplete="new-password"
              className="rounded-lg border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-primary-400 focus:border-primary-400"
            />
            
            {/* Verification Method Selection */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-gray-700">
                Verification Method
              </label>
              <div className="flex gap-3">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="verificationMethod"
                    value="whatsapp"
                    checked={verificationMethod === 'whatsapp'}
                    onChange={(e) => setVerificationMethod(e.target.value)}
                    className="text-primary-600 focus:ring-primary-500"
                  />
                  <span className="text-sm text-gray-700">WhatsApp</span>
                </label>
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="verificationMethod"
                    value="sms"
                    checked={verificationMethod === 'sms'}
                    onChange={(e) => setVerificationMethod(e.target.value)}
                    className="text-primary-600 focus:ring-primary-500"
                  />
                  <span className="text-sm text-gray-700">SMS</span>
                </label>
              </div>
              <p className="text-xs text-gray-500">
                {verificationMethod === 'whatsapp' 
                  ? 'OTP will be sent via WhatsApp (recommended)' 
                  : 'OTP will be sent via SMS'
                }
              </p>
            </div>
            
            <Button 
              onClick={handleSendOTP} 
              loading={otpLoading} 
              className="w-full mt-2 bg-gradient-to-r from-primary-600 to-pink-500 hover:from-primary-700 hover:to-pink-600 text-white font-semibold py-3 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-md"
            >
              Send OTP via {verificationMethod === 'whatsapp' ? 'WhatsApp' : 'SMS'}
            </Button>
          </div>
        )}

        {step === 2 && (
          <div className="flex flex-col gap-4">
            <div className="text-center mb-4">
              <p className="text-sm text-gray-600">
                We've sent a 6-digit OTP to <span className="font-semibold">{form.mobile}</span>
              </p>
              <p className="text-xs text-gray-500 mt-1">
                Enter the OTP to verify your mobile number
              </p>
            </div>
            <Input
              label="OTP"
              name="otp"
              type="text"
              value={form.otp}
              onChange={handleChange}
              error={errors.otp}
              placeholder="Enter 6-digit OTP"
              maxLength="6"
              pattern="[0-9]*"
              inputMode="numeric"
              className="rounded-lg border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-primary-400 focus:border-primary-400"
            />
            <div className="flex gap-2">
              <Button 
                onClick={() => setStep(1)} 
                className="flex-1 bg-gray-500 hover:bg-gray-600 text-white font-semibold py-3 rounded-xl transition-all duration-300"
              >
                Back
              </Button>
              <Button 
                onClick={handleVerifyOTP} 
                loading={otpLoading} 
                className="flex-1 bg-gradient-to-r from-primary-600 to-pink-500 hover:from-primary-700 hover:to-pink-600 text-white font-semibold py-3 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-md"
              >
                Verify OTP
              </Button>
            </div>
          </div>
        )}

        {step === 3 && (
          <form onSubmit={handleSubmit} className="flex flex-col gap-4">
            <div className="text-center mb-4">
              <p className="text-sm text-green-600 font-semibold">
                ✓ Mobile number verified successfully!
              </p>
              <p className="text-xs text-gray-500 mt-1">
                Complete your registration
              </p>
            </div>
            <Button type="submit" loading={loading} className="w-full mt-2 bg-gradient-to-r from-primary-600 to-pink-500 hover:from-primary-700 hover:to-pink-600 text-white font-semibold py-3 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-md">
              Complete Registration
            </Button>
          </form>
        )}

        {step === 1 && (
          <div className="text-center mt-4 text-sm">
            Already have an account? <a href="/login" className="text-primary-600 hover:underline font-semibold">Login</a>
          </div>
        )}
      </div>
    </div>
  );
}
