import React, { useState } from 'react';
import Input from './Input';
import Button from './Button';

export default function LootForm({ initialValues = {}, onSubmit, onCancel, loading }) {
  const [form, setForm] = useState({
    title: initialValues.title || '',
    description: initialValues.description || '',
    max_amount: initialValues.max_amount || '',
    redirect_url: initialValues.redirect_url || '',
    is_active: initialValues.is_active !== undefined ? initialValues.is_active : true,
  });
  const [errors, setErrors] = useState({});

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(f => ({ ...f, [name]: type === 'checkbox' ? checked : value }));
    setErrors(e => ({ ...e, [name]: '' }));
  };

  const validate = () => {
    const errs = {};
    if (!form.title) errs.title = 'Title is required';
    if (!form.max_amount || isNaN(form.max_amount) || Number(form.max_amount) <= 0) errs.max_amount = 'Enter a valid amount';
    if (!form.redirect_url) errs.redirect_url = 'Redirect URL is required';
    return errs;
  };

  const handleSubmit = e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    onSubmit(form);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <Input label="Title" name="title" value={form.title} onChange={handleChange} error={errors.title} />
      <Input label="Description" name="description" value={form.description} onChange={handleChange} error={errors.description} />
      <Input label="Max Amount" name="max_amount" type="number" min="1" value={form.max_amount} onChange={handleChange} error={errors.max_amount} />
      <Input label="Redirect URL" name="redirect_url" value={form.redirect_url} onChange={handleChange} error={errors.redirect_url} />
      <div className="flex items-center gap-2">
        <input type="checkbox" name="is_active" checked={!!form.is_active} onChange={handleChange} id="is_active" />
        <label htmlFor="is_active" className="text-sm">Active</label>
      </div>
      <div className="flex gap-2">
        <Button type="submit" loading={loading} className="flex-1">Save</Button>
        <Button type="button" variant="secondary" onClick={onCancel} className="flex-1">Cancel</Button>
      </div>
    </form>
  );
}
