import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import LootCard from '../components/LootCard';
import BalanceCard from '../components/BalanceCard';
import ReferralCard from '../components/ReferralCard';
import Input from '../components/Input';
import Button from '../components/Button';
import toast from 'react-hot-toast';
import ChatBox from '../components/ChatBox';
import moment from 'moment-timezone';

export default function ClientDashboard() {
  const [loots, setLoots] = useState([]);
  const [balance, setBalance] = useState({ balance: 0, total_earned: 0 });
  const [investments, setInvestments] = useState([]);
  const [referrals, setReferrals] = useState([]);
  const [withdrawals, setWithdrawals] = useState([]);
  const [showInvestModal, setShowInvestModal] = useState(false);
  const [showWithdrawModal, setShowWithdrawModal] = useState(false);
  const [selectedLoot, setSelectedLoot] = useState(null);
  const [investAmount, setInvestAmount] = useState('');
  const [investError, setInvestError] = useState('');
  const [withdrawAmount, setWithdrawAmount] = useState('');
  const [withdrawUpiId, setWithdrawUpiId] = useState('');
  const [withdrawError, setWithdrawError] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');

  const TABS = [
    { key: 'dashboard', label: 'Dashboard' },
    { key: 'withdrawals', label: 'Withdrawal History' },
    { key: 'referrals', label: 'Referral History' }
  ];

  useEffect(() => {
    fetchLoots();
    fetchBalance();
    fetchInvestments();
    fetchReferrals();
    fetchWithdrawals();
  }, []);

  const fetchLoots = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/loots', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Loots fetched:', data.data);
      setLoots(data.data);
    } else {
      console.error('Failed to fetch loots:', data.message);
    }
  };
  const fetchBalance = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/balance', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setBalance(data.data);
  };
  const fetchInvestments = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/investments', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Investments fetched:', data.data);
      setInvestments(data.data);
    } else {
      console.error('Failed to fetch investments:', data.message);
    }
  };
  const fetchReferrals = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/referrals', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setReferrals(data.data);
  };

  const fetchWithdrawals = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/withdrawals', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setWithdrawals(data.data);
  };



  const handleInvest = loot => {
    console.log('Investing in loot:', loot);
    setSelectedLoot(loot);
    setInvestAmount('');
    setInvestError('');
    setShowInvestModal(true);
  };

  const submitInvest = async e => {
    e.preventDefault();
    setInvestError('');
    console.log('Submitting investment:', { loot_id: selectedLoot.id, amount: investAmount });
    
    if (!investAmount || isNaN(investAmount) || Number(investAmount) <= 0) {
      setInvestError('Enter a valid amount');
      return;
    }
    if (Number(investAmount) > selectedLoot.max_amount) {
      setInvestError(`Amount cannot exceed ₹${selectedLoot.max_amount}`);
      return;
    }
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/client/invest', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ loot_id: selectedLoot.id, amount: investAmount }),
      });
      const data = await res.json();
      console.log('Investment response:', data);
      if (!data.success) throw new Error(data.message);
      toast.success('Investment successful!');
      setShowInvestModal(false);
      fetchInvestments();
      fetchBalance();
    } catch (err) {
      console.error('Investment error:', err);
      setInvestError(err.message || 'Investment failed');
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = () => {
    setWithdrawAmount('');
    setWithdrawUpiId('');
    setWithdrawError('');
    setShowWithdrawModal(true);
  };

  const submitWithdraw = async (e) => {
    e.preventDefault();
    setWithdrawError('');
    
    if (!withdrawAmount || isNaN(withdrawAmount) || Number(withdrawAmount) <= 0) {
      setWithdrawError('Enter a valid amount');
      return;
    }
    
    if (Number(withdrawAmount) > balance.balance) {
      setWithdrawError('Cannot withdraw more than balance');
      return;
    }
    
    if (!withdrawUpiId || withdrawUpiId.trim() === '') {
      setWithdrawError('UPI ID is required');
      return;
    }
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/client/withdraw', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ 
          amount: withdrawAmount, 
          upi_id: withdrawUpiId.trim() 
        }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Withdrawal request submitted!');
      setShowWithdrawModal(false);
      fetchBalance();
      fetchWithdrawals();
    } catch (err) {
      setWithdrawError(err.message || 'Withdrawal failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="max-w-3xl mx-auto py-8 px-2">
        {/* Tab Navigation */}
        <div className="flex gap-2 mb-6 border-b">
          {TABS.map(tab => (
            <button
              key={tab.key}
              className={`px-4 py-2 font-medium border-b-2 transition-colors duration-150 ${
                activeTab === tab.key 
                  ? 'border-primary-600 text-primary-700' 
                  : 'border-transparent text-gray-500 hover:text-primary-600'
              }`}
              onClick={() => setActiveTab(tab.key)}
            >
              {tab.label}
            </button>
          ))}
        </div>

        {/* Dashboard Tab */}
        {activeTab === 'dashboard' && (
          <div>
            <BalanceCard balance={balance.balance} totalEarned={balance.total_earned} />
            
            <h2 className="text-xl font-bold mb-2">Available Loots</h2>
            {loots.length === 0 && <div className="text-gray-500 mb-4">No loots available.</div>}
            {loots.map(loot => (
              <LootCard 
                key={loot.id} 
                loot={loot} 
                onInvest={handleInvest} 
                disabled={false} 
              />
            ))}
            
            <h2 className="text-xl font-bold mt-8 mb-2">Your Referral Links</h2>
            {investments.length === 0 && <div className="text-gray-500 mb-4">No investments yet.</div>}
            {investments.map(inv => (
              <ReferralCard
                key={inv.id}
                referralUrl={window.location.origin + '/referral/' + inv.referral_code}
                lootTitle={`${inv.loot_title} - Investment #${inv.id}`}
                investmentAmount={`₹${inv.amount}`}
              />
            ))}
          </div>
        )}

        {/* Withdrawal History Tab */}
        {activeTab === 'withdrawals' && (
          <div>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Withdrawal History</h2>
              <Button variant="primary" onClick={handleWithdraw}>New Withdrawal</Button>
            </div>
            
            {/* Withdrawal Statistics */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="card p-4">
                <div className="text-xs text-gray-500">Total Withdrawals</div>
                <div className="text-2xl font-bold">{withdrawals.length}</div>
              </div>
              <div className="card p-4">
                <div className="text-xs text-gray-500">Total Amount</div>
                <div className="text-2xl font-bold">₹{withdrawals.reduce((sum, w) => sum + Number(w.amount), 0).toFixed(2)}</div>
              </div>
              <div className="card p-4">
                <div className="text-xs text-gray-500">Pending Requests</div>
                <div className="text-2xl font-bold">{withdrawals.filter(w => w.status === 'pending').length}</div>
              </div>
            </div>
            
            {withdrawals.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-gray-500 mb-4">No withdrawals yet.</div>
                <Button variant="primary" onClick={handleWithdraw}>Make Your First Withdrawal</Button>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full bg-white rounded shadow">
                  <thead>
                    <tr>
                      <th className="px-4 py-2">Amount</th>
                      <th className="px-4 py-2">UPI ID</th>
                      <th className="px-4 py-2">Status</th>
                      <th className="px-4 py-2">Reference</th>
                      <th className="px-4 py-2">Notes</th>
                      <th className="px-4 py-2">Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {withdrawals.map(w => (
                      <tr key={w.id} className="border-t">
                        <td className="px-4 py-2 font-medium">₹{w.amount}</td>
                        <td className="px-4 py-2">{w.upi_id || '-'}</td>
                        <td className="px-4 py-2">
                          <span className={`badge ${w.status === 'approved' ? 'badge-success' : w.status === 'rejected' ? 'badge-danger' : 'badge-warning'}`}>
                            {w.status}
                          </span>
                        </td>
                        <td className="px-4 py-2">{w.reference_number || '-'}</td>
                        <td className="px-4 py-2">{w.notes || '-'}</td>
                        <td className="px-4 py-2">{moment(w.created_at).tz('Asia/Kolkata').format('DD/MM/YYYY h:mm A')}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {/* Referral History Tab */}
        {activeTab === 'referrals' && (
          <div>
            <h2 className="text-xl font-bold mb-4">Referral History</h2>
            
            {/* Referral Statistics */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="card p-4">
                <div className="text-xs text-gray-500">Total Referrals</div>
                <div className="text-2xl font-bold">{referrals.length}</div>
              </div>
              <div className="card p-4">
                <div className="text-xs text-gray-500">Total Earned</div>
                <div className="text-2xl font-bold">₹{referrals.reduce((sum, r) => sum + Number(r.amount), 0).toFixed(2)}</div>
              </div>
              <div className="card p-4">
                <div className="text-xs text-gray-500">Completed</div>
                <div className="text-2xl font-bold">{referrals.filter(r => r.status === 'completed').length}</div>
              </div>
            </div>
            
            {referrals.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-gray-500 mb-4">No referrals yet.</div>
                <div className="text-sm text-gray-400">Share your referral links to earn more!</div>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full bg-white rounded shadow">
                  <thead>
                    <tr>
                      <th className="px-4 py-2">Loot</th>
                      <th className="px-4 py-2">Customer UPI</th>
                      <th className="px-4 py-2">Amount</th>
                      <th className="px-4 py-2">Status</th>
                      <th className="px-4 py-2">Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {referrals.map(r => (
                      <tr key={r.id} className="border-t">
                        <td className="px-4 py-2">{r.loot_title}</td>
                        <td className="px-4 py-2">{r.customer_upi}</td>
                        <td className="px-4 py-2 font-medium">₹{r.amount}</td>
                        <td className="px-4 py-2">
                          <span className={`badge ${r.status === 'completed' ? 'badge-success' : 'badge-warning'}`}>
                            {r.status}
                          </span>
                        </td>
                        <td className="px-4 py-2">{moment(r.created_at).tz('Asia/Kolkata').format('DD/MM/YYYY h:mm A')}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}
      </div>
      {/* Invest Modal */}
      {showInvestModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <form onSubmit={submitInvest} className="bg-white p-6 rounded shadow w-full max-w-sm">
            <h2 className="text-lg font-bold mb-2">Invest in {selectedLoot.title}</h2>
            <p className="text-sm text-gray-600 mb-4">
              Each investment generates a unique referral link that you can share to earn more!
            </p>
            <Input
              label={`Amount (max: ₹${selectedLoot.max_amount})`}
              type="number"
              min="1"
              max={selectedLoot.max_amount}
              value={investAmount}
              onChange={e => setInvestAmount(e.target.value)}
              error={investError}
              autoFocus
            />
            <div className="flex gap-2 mt-4">
              <Button type="submit" loading={loading} className="flex-1">Invest & Get Referral Link</Button>
              <Button type="button" variant="secondary" onClick={() => setShowInvestModal(false)} className="flex-1">Cancel</Button>
            </div>
          </form>
        </div>
      )}

      {/* Withdrawal Modal */}
      {showWithdrawModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <form onSubmit={submitWithdraw} className="bg-white p-6 rounded shadow w-full max-w-sm">
            <h2 className="text-lg font-bold mb-2">Withdraw Funds</h2>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Amount (max: ₹{balance.balance})
              </label>
              <input
                type="number"
                min="1"
                max={balance.balance}
                value={withdrawAmount}
                onChange={e => setWithdrawAmount(e.target.value)}
                className={`input ${withdrawError ? 'border-danger-500' : ''}`}
                placeholder="Enter amount"
                autoFocus
              />
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                UPI ID
              </label>
              <input
                type="text"
                value={withdrawUpiId}
                onChange={e => setWithdrawUpiId(e.target.value)}
                className={`input ${withdrawError ? 'border-danger-500' : ''}`}
                placeholder="Enter your UPI ID"
              />
            </div>
            {withdrawError && (
              <p className="text-danger-600 text-xs mb-4">{withdrawError}</p>
            )}
            <div className="flex gap-2">
              <Button type="submit" loading={loading} className="flex-1">Withdraw</Button>
              <Button type="button" variant="secondary" onClick={() => setShowWithdrawModal(false)} className="flex-1">Cancel</Button>
            </div>
          </form>
        </div>
      )}
      {/* Chat with Admin */}
      <div className="mt-12">
        <ChatBox />
      </div>
    </div>
  );
}
