import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import LootCard from '../components/LootCard';
import ReferralCard from '../components/ReferralCard';
import Button from '../components/Button';
import toast from 'react-hot-toast';
import ChatBox from '../components/ChatBox';
import moment from 'moment-timezone';

export default function CustomOffers() {
  const [loots, setLoots] = useState([]);
  const [investments, setInvestments] = useState([]);
  const [showInvestModal, setShowInvestModal] = useState(false);
  const [selectedLoot, setSelectedLoot] = useState(null);
  const [customerAmount, setCustomerAmount] = useState('');
  const [earnAmount, setEarnAmount] = useState('');
  const [investError, setInvestError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLoots();
    fetchInvestments();
  }, []);

  const fetchLoots = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/loots', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Loots fetched:', data.data);
      setLoots(data.data);
    } else {
      console.error('Failed to fetch loots:', data.message);
    }
  };

  const fetchInvestments = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/client/investments', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      console.log('Investments fetched:', data.data);
      setInvestments(data.data);
    } else {
      console.error('Failed to fetch investments:', data.message);
    }
  };

  const handleInvest = loot => {
    console.log('Investing in loot:', loot);
    setSelectedLoot(loot);
    setCustomerAmount('');
    setEarnAmount('');
    setInvestError('');
    setShowInvestModal(true);
  };

  const submitInvest = async e => {
    e.preventDefault();
    setInvestError('');
    
    // Validation
    if (!customerAmount || isNaN(customerAmount) || Number(customerAmount) <= 0) {
      setInvestError('Enter a valid customer amount');
      return;
    }
    
    if (!earnAmount || isNaN(earnAmount) || Number(earnAmount) <= 0) {
      setInvestError('Enter a valid earn amount');
      return;
    }
    
    const totalAmount = Number(customerAmount) + Number(earnAmount);
    
    if (totalAmount > selectedLoot.max_amount) {
      setInvestError(`Total amount (₹${totalAmount}) cannot exceed ₹${selectedLoot.max_amount}`);
      return;
    }
    
    if (Number(earnAmount) >= Number(customerAmount)) {
      setInvestError('Earn amount should be less than customer amount');
      return;
    }
    
    console.log('Submitting custom investment:', { 
      loot_id: selectedLoot.id, 
      customer_amount: customerAmount,
      earn_amount: earnAmount,
      total_amount: totalAmount
    });
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const requestBody = { 
        loot_id: selectedLoot.id, 
        customer_amount: Number(customerAmount),
        earn_amount: Number(earnAmount),
        total_amount: totalAmount
      };
      
      console.log('Sending request to /api/client/invest with body:', requestBody);
      console.log('Token:', token ? 'Present' : 'Missing');
      
      const res = await fetch('/api/client/invest', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody),
      });
      
      console.log('Response status:', res.status);
      console.log('Response headers:', res.headers);
      
      const data = await res.json();
      console.log('Investment response:', data);
      if (!data.success) throw new Error(data.message);
      toast.success('Custom offer created successfully!');
      setShowInvestModal(false);
      fetchInvestments();
    } catch (err) {
      console.error('Investment error:', err);
      setInvestError(err.message || 'Investment failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="max-w-4xl mx-auto py-8 px-2">
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Custom Offers</h1>
          <p className="text-gray-600 mt-2">Create and manage your custom investment offers</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Available Loots Section */}
          <div>
            <h2 className="text-xl font-bold mb-4">Available Loots</h2>
            {loots.length === 0 && <div className="text-gray-500 mb-4">No loots available.</div>}
            {loots.map(loot => (
              <LootCard 
                key={loot.id} 
                loot={loot} 
                onInvest={handleInvest} 
                disabled={false} 
              />
            ))}
          </div>

          {/* Your Custom Offers Section */}
          <div>
            <h2 className="text-xl font-bold mb-4">Your Custom Offers</h2>
            {investments.length === 0 && <div className="text-gray-500 mb-4">No custom offers yet.</div>}
            <div className="overflow-x-auto mb-6">
              <table className="min-w-full bg-white rounded shadow">
                <thead>
                  <tr>
                    <th className="px-4 py-2">Loot</th>
                    <th className="px-4 py-2">Customer Earns</th>
                    <th className="px-4 py-2">You Earn</th>
                    <th className="px-4 py-2">Total</th>
                    <th className="px-4 py-2">Referrals</th>
                    <th className="px-4 py-2">Date</th>
                  </tr>
                </thead>
                <tbody>
                  {investments.map(inv => (
                    <tr key={inv.id} className="border-t">
                      <td className="px-4 py-2">{inv.loot_title}</td>
                      <td className="px-4 py-2">₹{inv.customer_amount || inv.amount}</td>
                      <td className="px-4 py-2">₹{inv.earn_amount || 0}</td>
                      <td className="px-4 py-2">₹{inv.total_amount || inv.amount}</td>
                      <td className="px-4 py-2">{inv.referral_count || 0}</td>
                      <td className="px-4 py-2">{moment(inv.created_at).tz('Asia/Kolkata').format('DD/MM/YYYY h:mm A')}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            <h2 className="text-xl font-bold mt-8 mb-4">Your Referral Links</h2>
            {investments.length === 0 && <div className="text-gray-500 mb-4">No referral links yet.</div>}
            {investments.map(inv => (
              <ReferralCard
                key={inv.id}
                referralUrl={window.location.origin + '/referral/' + inv.referral_code}
                lootTitle={`${inv.loot_title} - Investment #${inv.id}`}
                investmentAmount={`₹${inv.customer_amount || inv.amount} + ₹${inv.earn_amount || 0}`}
              />
            ))}
          </div>
        </div>
      </div>

      {/* Invest Modal */}
      {showInvestModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <form onSubmit={submitInvest} className="bg-white p-6 rounded shadow w-full max-w-md">
            <h2 className="text-lg font-bold mb-2">Create Custom Offer</h2>
            <p className="text-sm text-gray-600 mb-4">
              Set how much the customer earns and how much you earn (max: ₹{selectedLoot.max_amount})
            </p>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Customer Amount (what customer earns)
              </label>
              <input
                type="number"
                min="1"
                max={selectedLoot.max_amount}
                value={customerAmount}
                onChange={e => setCustomerAmount(e.target.value)}
                className={`input ${investError ? 'border-danger-500' : ''}`}
                placeholder="Enter customer amount"
                autoFocus
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Your Earn Amount
              </label>
              <input
                type="number"
                min="1"
                max={selectedLoot.max_amount}
                value={earnAmount}
                onChange={e => setEarnAmount(e.target.value)}
                className={`input ${investError ? 'border-danger-500' : ''}`}
                placeholder="Enter your earn amount"
              />
            </div>
            
            {customerAmount && earnAmount && (
              <div className="mb-4 p-3 bg-gray-50 rounded">
                <div className="text-sm text-gray-600">
                  <div>Customer earns: ₹{customerAmount}</div>
                  <div>You earn: ₹{earnAmount}</div>
                  <div className="font-medium">Total: ₹{Number(customerAmount) + Number(earnAmount)}</div>
                </div>
              </div>
            )}
            
            {investError && (
              <p className="text-danger-600 text-xs mb-4">{investError}</p>
            )}
            
            <div className="flex gap-2">
              <Button type="submit" loading={loading} className="flex-1">Create Offer</Button>
              <Button type="button" variant="secondary" onClick={() => setShowInvestModal(false)} className="flex-1">Cancel</Button>
            </div>
          </form>
        </div>
      )}

      {/* Chat with Admin */}
      <div className="mt-12">
        <ChatBox />
      </div>
    </div>
  );
}
