import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Register() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState('register'); // 'register' | 'verify' | 'success'
  const [verifyCode, setVerifyCode] = useState('');
  const [verifyError, setVerifyError] = useState('');
  const [registeredEmail, setRegisteredEmail] = useState('');

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.username) errs.username = 'Username is required';
    if (!form.email) errs.email = 'Email is required';
    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'Password must be at least 6 characters';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      const res = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      setRegisteredEmail(form.email);
      setStep('verify');
      toast.success('Registration successful! Please check your email for the verification code.');
    } catch (err) {
      toast.error(err.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  const handleVerify = async e => {
    e.preventDefault();
    setVerifyError('');
    if (!verifyCode) {
      setVerifyError('Verification code is required');
      return;
    }
    setLoading(true);
    try {
      const res = await fetch('/api/auth/verify-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: registeredEmail, code: verifyCode }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      setStep('success');
      toast.success('Email verified! You can now log in.');
    } catch (err) {
      setVerifyError(err.message || 'Verification failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      {step === 'register' && (
        <form onSubmit={handleSubmit} className="w-full max-w-sm bg-white p-8 rounded shadow">
          <h1 className="text-3xl font-bold mb-6 text-center">Register</h1>
          <Input
            label="Username"
            name="username"
            value={form.username}
            onChange={handleChange}
            error={errors.username}
            autoComplete="username"
          />
          <Input
            label="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            error={errors.email}
            autoComplete="email"
          />
          <Input
            label="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            error={errors.password}
            autoComplete="new-password"
          />
          <Button type="submit" loading={loading} className="w-full mt-2">Register</Button>
          <div className="text-center mt-4 text-sm">
            Already have an account? <a href="/login" className="text-primary-600 hover:underline">Login</a>
          </div>
        </form>
      )}
      {step === 'verify' && (
        <form onSubmit={handleVerify} className="w-full max-w-sm bg-white p-8 rounded shadow">
          <h1 className="text-2xl font-bold mb-6 text-center">Verify Your Email</h1>
          <p className="mb-4 text-gray-600 text-center">Enter the 6-digit code sent to <span className="font-semibold">{registeredEmail}</span></p>
          <Input
            label="Verification Code"
            name="verificationCode"
            value={verifyCode}
            onChange={e => setVerifyCode(e.target.value)}
            error={verifyError}
            autoFocus
            maxLength={6}
          />
          <Button type="submit" loading={loading} className="w-full mt-2">Verify</Button>
          <div className="text-center mt-4 text-sm">
            Didn't get the code? Check your spam folder or <span className="text-primary-600 font-semibold cursor-pointer" onClick={handleSubmit}>Resend</span>
          </div>
        </form>
      )}
      {step === 'success' && (
        <div className="w-full max-w-sm bg-white p-8 rounded shadow text-center">
          <h1 className="text-2xl font-bold mb-4 text-green-600">Email Verified!</h1>
          <p className="mb-6 text-gray-700">Your account has been verified. You can now log in.</p>
          <Button className="w-full" onClick={() => navigate('/login')}>Go to Login</Button>
        </div>
      )}
    </div>
  );
}
