import React, { useEffect, useRef, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import io from 'socket.io-client';
import Button from './Button';

const socket = io('/', { autoConnect: false });

export default function ChatBox() {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!user) return;
    
    socket.connect();
    socket.emit('join-client', user.id);
    fetchMessages();
    
    socket.on('new-message', msg => {
      setMessages(m => [...m, msg]);
    });
    
    socket.on('connect', () => {
      console.log('Connected to chat server');
    });
    
    socket.on('connect_error', (error) => {
      console.error('Chat connection error:', error);
    });
    
    return () => {
      socket.off('new-message');
      socket.off('connect');
      socket.off('connect_error');
      socket.disconnect();
    };
    // eslint-disable-next-line
  }, [user]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const fetchMessages = async () => {
    setLoading(true);
    const res = await fetch('/api/chat/messages');
    const data = await res.json();
    if (data.success) setMessages(data.data);
    setLoading(false);
  };

  const sendMessage = async e => {
    e.preventDefault();
    if (!input.trim()) return;
    const msg = {
      message: input,
      sender_id: user.id,
      toAdmin: true,
      created_at: new Date().toISOString(),
      sender_name: user.username,
      sender_role: user.role,
    };
    setMessages(m => [...m, msg]);
    setInput('');
    socket.emit('send-message', msg);
    await fetch('/api/chat/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: input }),
    });
  };

  return (
    <div className="card mt-8 max-w-xl mx-auto">
      <div className="card-header">Chat with Admin</div>
      <div className="card-body h-64 overflow-y-auto bg-gray-50 rounded border border-gray-200">
        {loading ? (
          <div className="text-center text-gray-500">Loading...</div>
        ) : (
          <div className="flex flex-col gap-2">
            {messages.map((msg, i) => (
              <div key={i} className={`flex ${msg.sender_role === 'admin' ? 'justify-start' : 'justify-end'}`}>
                <div className={`px-3 py-2 rounded-lg max-w-xs ${msg.sender_role === 'admin' ? 'bg-primary-100 text-primary-900' : 'bg-success-100 text-success-900'}`}>
                  <div className="text-xs font-semibold mb-1">{msg.sender_name} <span className="text-gray-400">({msg.sender_role})</span></div>
                  <div>{msg.message}</div>
                  <div className="text-xs text-gray-400 mt-1">{new Date(msg.created_at).toLocaleTimeString()}</div>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>
      <form onSubmit={sendMessage} className="flex gap-2 mt-2">
        <input
          className="input flex-1"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
        />
        <Button type="submit" variant="primary">Send</Button>
      </form>
    </div>
  );
}
