import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import LootCard from '../components/LootCard';
import BalanceCard from '../components/BalanceCard';
import ReferralCard from '../components/ReferralCard';
import Input from '../components/Input';
import Button from '../components/Button';
import toast from 'react-hot-toast';
import ChatBox from '../components/ChatBox';

export default function ClientDashboard() {
  const [loots, setLoots] = useState([]);
  const [balance, setBalance] = useState({ balance: 0, total_earned: 0 });
  const [investments, setInvestments] = useState([]);
  const [referrals, setReferrals] = useState([]);
  const [showInvestModal, setShowInvestModal] = useState(false);
  const [selectedLoot, setSelectedLoot] = useState(null);
  const [investAmount, setInvestAmount] = useState('');
  const [investError, setInvestError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLoots();
    fetchBalance();
    fetchInvestments();
    fetchReferrals();
  }, []);

  const fetchLoots = async () => {
    const res = await fetch('/api/client/loots');
    const data = await res.json();
    if (data.success) setLoots(data.data);
  };
  const fetchBalance = async () => {
    const res = await fetch('/api/client/balance');
    const data = await res.json();
    if (data.success) setBalance(data.data);
  };
  const fetchInvestments = async () => {
    const res = await fetch('/api/client/investments');
    const data = await res.json();
    if (data.success) setInvestments(data.data);
  };
  const fetchReferrals = async () => {
    const res = await fetch('/api/client/referrals');
    const data = await res.json();
    if (data.success) setReferrals(data.data);
  };

  const handleInvest = loot => {
    setSelectedLoot(loot);
    setInvestAmount('');
    setInvestError('');
    setShowInvestModal(true);
  };

  const submitInvest = async e => {
    e.preventDefault();
    setInvestError('');
    if (!investAmount || isNaN(investAmount) || Number(investAmount) <= 0) {
      setInvestError('Enter a valid amount');
      return;
    }
    if (Number(investAmount) > selectedLoot.max_amount) {
      setInvestError(`Amount cannot exceed ₹${selectedLoot.max_amount}`);
      return;
    }
    setLoading(true);
    try {
      const res = await fetch('/api/client/invest', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ loot_id: selectedLoot.id, amount: investAmount }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Investment successful!');
      setShowInvestModal(false);
      fetchInvestments();
    } catch (err) {
      setInvestError(err.message || 'Investment failed');
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = async () => {
    const amount = prompt('Enter amount to withdraw (max: ' + balance.balance + ')');
    if (!amount || isNaN(amount) || Number(amount) <= 0) return;
    if (Number(amount) > balance.balance) {
      toast.error('Cannot withdraw more than balance');
      return;
    }
    setWithdrawLoading(true);
    try {
      const res = await fetch('/api/client/withdraw', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ amount }),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.message);
      toast.success('Withdrawal request submitted!');
      fetchBalance();
    } catch (err) {
      toast.error(err.message || 'Withdraw failed');
    } finally {
      setWithdrawLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="max-w-3xl mx-auto py-8 px-2">
        <BalanceCard balance={balance.balance} totalEarned={balance.total_earned} onWithdraw={handleWithdraw} />
        <h2 className="text-xl font-bold mb-2">Available Loots</h2>
        {loots.length === 0 && <div className="text-gray-500 mb-4">No loots available.</div>}
        {loots.map(loot => (
          <LootCard key={loot.id} loot={loot} onInvest={handleInvest} disabled={!!investments.find(i => i.loot_id === loot.id)} />
        ))}
        <h2 className="text-xl font-bold mt-8 mb-2">Your Referral Links</h2>
        {investments.length === 0 && <div className="text-gray-500 mb-4">No investments yet.</div>}
        {investments.map(inv => (
          <ReferralCard
            key={inv.id}
            referralUrl={window.location.origin + '/referral/' + inv.referral_code}
            lootTitle={inv.loot_title}
          />
        ))}
        <h2 className="text-xl font-bold mt-8 mb-2">Referral History</h2>
        {referrals.length === 0 && <div className="text-gray-500 mb-4">No referrals yet.</div>}
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded shadow">
            <thead>
              <tr>
                <th className="px-4 py-2">Loot</th>
                <th className="px-4 py-2">UPI</th>
                <th className="px-4 py-2">Amount</th>
                <th className="px-4 py-2">Status</th>
                <th className="px-4 py-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {referrals.map(r => (
                <tr key={r.id} className="border-t">
                  <td className="px-4 py-2">{r.loot_title}</td>
                  <td className="px-4 py-2">{r.customer_upi}</td>
                  <td className="px-4 py-2">₹{r.amount}</td>
                  <td className="px-4 py-2">
                    <span className={`badge ${r.status === 'completed' ? 'badge-success' : 'badge-warning'}`}>{r.status}</span>
                  </td>
                  <td className="px-4 py-2">{new Date(r.created_at).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {/* Invest Modal */}
      {showInvestModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <form onSubmit={submitInvest} className="bg-white p-6 rounded shadow w-full max-w-sm">
            <h2 className="text-lg font-bold mb-2">Invest in {selectedLoot.title}</h2>
            <Input
              label={`Amount (max: ₹${selectedLoot.max_amount})`}
              type="number"
              min="1"
              max={selectedLoot.max_amount}
              value={investAmount}
              onChange={e => setInvestAmount(e.target.value)}
              error={investError}
              autoFocus
            />
            <div className="flex gap-2 mt-4">
              <Button type="submit" loading={loading} className="flex-1">Invest</Button>
              <Button type="button" variant="secondary" onClick={() => setShowInvestModal(false)} className="flex-1">Cancel</Button>
            </div>
          </form>
        </div>
      )}
      {/* Chat with Admin */}
      <div className="mt-12">
        <ChatBox />
      </div>
    </div>
  );
}
