import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

export default function Login() {
  const auth = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  if (auth.loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  const { login } = auth;

  const handleChange = e => {
    if (e && e.target && e.target.name) {
      setForm(f => ({ ...f, [e.target.name]: e.target.value }));
      setErrors(err => ({ ...err, [e.target.name]: '' }));
    }
  };

  const validate = () => {
    const errs = {};
    if (!form.email) errs.email = 'Email is required';
    return errs;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);
    setLoading(true);
    try {
      // For demo, just show success and redirect
      toast.success('Login successful!');
      navigate('/dashboard');
    } catch (err) {
      toast.error('Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col md:flex-row">
      {/* Left: Gradient background with caption */}
      <div className="hidden md:flex flex-col justify-center items-center flex-1 bg-gradient-to-br from-pink-200 via-yellow-100 to-blue-200 relative">
        <div className="absolute top-6 left-6 flex items-center gap-2 text-lg font-semibold text-gray-700 opacity-80">
          <span className="inline-block w-8 h-8 bg-black/10 rounded-full flex items-center justify-center">
            <svg width="24" height="24" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" fill="#fff"/><circle cx="12" cy="12" r="8" fill="#a3a3a3"/></svg>
          </span>
          AWESOME SCREENSHOT & RECORDER
        </div>
        <div className="max-w-xl px-8">
          <h1 className="text-4xl md:text-5xl font-bold text-black mb-4 drop-shadow-lg" style={{lineHeight:1.1}}>
            A Smart Way to Earn
          </h1>
        </div>
      </div>
      {/* Right: Login card */}
      <div className="flex flex-1 items-center justify-center bg-white py-12 px-4">
        <div className="w-full max-w-md mx-auto bg-white rounded-xl shadow-xl p-8 border border-gray-100">
          <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">Welcome back</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">Email address</label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={form.email}
                onChange={handleChange}
                className="block w-full rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 text-gray-900 text-base"
                placeholder="Email address"
              />
              {errors.email && <div className="text-xs text-red-500 mt-1">{errors.email}</div>}
            </div>
            <Button
              type="submit"
              loading={loading}
              className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-2.5 rounded-md text-base shadow-sm transition-colors"
            >
              Continue
            </Button>
          </form>
          <div className="text-center text-sm text-gray-600 mt-4">
            Don't have an account?{' '}
            <a href="/register" className="text-primary-600 hover:underline font-medium">Sign up</a>
          </div>
          <div className="flex items-center my-6">
            <div className="flex-grow border-t border-gray-200"></div>
            <span className="mx-3 text-gray-400 text-sm">OR</span>
            <div className="flex-grow border-t border-gray-200"></div>
          </div>
          <div className="space-y-3">
            <button
              type="button"
              className="w-full flex items-center justify-center gap-3 border border-gray-300 rounded-md py-2.5 bg-white hover:bg-gray-50 text-gray-800 font-medium text-base shadow-sm transition-colors"
            >
              <img src="https://www.svgrepo.com/show/475656/google-color.svg" alt="Google" className="w-5 h-5" />
              Continue with Google
            </button>
            <button
              type="button"
              className="w-full flex items-center justify-center gap-3 border border-gray-300 rounded-md py-2.5 bg-white hover:bg-gray-50 text-gray-800 font-medium text-base shadow-sm transition-colors"
            >
              <img src="https://www.svgrepo.com/show/452213/microsoft.svg" alt="Microsoft" className="w-5 h-5" />
              Continue with Microsoft Account
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
