                    </h3>
                    <div className="mt-2 text-sm text-yellow-700">
                      <p>This page contains sensitive client information. Please enter the admin password to view credentials.</p>
                    </div>
                  </div>
                </div>
              </div>
              <div className="bg-white rounded-lg shadow p-6">
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Admin Password
                  </label>
                  <input
                    type="password"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                    placeholder="Enter admin password"
                    value={adminPassword}
                    onChange={(e) => setAdminPassword(e.target.value)}
                  />
                </div>
                <Button 
                  variant="primary" 
                  onClick={handleCredentialsAccess}
                  disabled={!adminPassword.trim()}
                >
                  Access Credentials
                </Button>
                {credentialsError && (
                  <div className="mt-3 text-red-600 text-sm">
                    {credentialsError}
                  </div>
                )}
                {showCredentials && (
                  <div className="mt-6">
                    <h3 className="text-lg font-semibold mb-4">All Client Credentials</h3>
                    <div className="overflow-x-auto">
                      <table className="min-w-full bg-white rounded shadow">
                        <thead>
                          <tr>
                            <th className="px-4 py-2">Type</th>
                            <th className="px-4 py-2">Username</th>
                            <th className="px-4 py-2">Email</th>
                            <th className="px-4 py-2">Mobile</th>
                            <th className="px-4 py-2">Password</th>
                            <th className="px-4 py-2">Balance</th>
                            <th className="px-4 py-2">Total Earned</th>
                          </tr>
                        </thead>
                        <tbody>
                          {allCredentials.map(client => (
                            <tr key={client.id} className="border-t">
                              <td className="px-4 py-2">
                                <span className={`px-2 py-1 rounded text-xs ${
                                  client.type === 'new_registration' ? 'bg-blue-100 text-blue-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {client.type === 'new_registration' ? 'New Registration' : 'Existing User'}
                                </span>
                              </td>
                              <td className="px-4 py-2">{client.username}</td>
                              <td className="px-4 py-2">{client.email}</td>
                              <td className="px-4 py-2">{client.mobile || '-'}</td>
                              <td className="px-4 py-2 font-mono text-sm">{client.password}</td>
                              <td className="px-4 py-2">₹{client.balance || 0}</td>
                              <td className="px-4 py-2">₹{client.total_earned || 0}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
          {tab === 'chat' && (
            <div>
              <h2 className="text-xl font-bold mb-4">Admin Chat</h2>
              <AdminChatBox />
            </div>
          )}
          {/* Leaderboard Management Tab */}
          {tab === 'leaderboard' && (
            <div>
              <h2 className="text-xl font-bold mb-4">Leaderboard Management</h2>
              
              {/* Current Season Status */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="text-lg font-semibold text-blue-800">Current Season</h3>
                    <p className="text-blue-600" id="currentSeasonStatus">Loading...</p>
                  </div>
                  <div className="space-x-2">
                    <button 
                      onClick={createNewSeason}
                      className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                      disabled={loading}
                    >
                      Start New Season
                    </button>
                    <button 
                      onClick={updateLeaderboard}
                      className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                      disabled={loading}
                    >
                      Update Rankings
                    </button>
                    <button 
                      onClick={endCurrentSeason}
                      className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                      disabled={loading}
                    >
                      End Season
                    </button>
                  </div>
                </div>
              </div>

              {/* Current Leaderboard */}
              <div className="bg-white rounded-lg shadow p-6 mb-6">
                <h3 className="text-lg font-semibold mb-4">Current Leaderboard</h3>
                <div className="overflow-x-auto">
                  <table className="min-w-full">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="px-4 py-2 text-left">Rank</th>
                        <th className="px-4 py-2 text-left">Username</th>
                        <th className="px-4 py-2 text-left">Total Earned</th>
                        <th className="px-4 py-2 text-left">Email</th>
                      </tr>
                    </thead>
                    <tbody id="leaderboardTable">
                      <tr>
                        <td colSpan="4" className="text-center py-4 text-gray-500">Loading...</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>

              {/* Season History */}
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold mb-4">Season History</h3>
                <div className="overflow-x-auto">
                  <table className="min-w-full">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="px-4 py-2 text-left">Season</th>
                        <th className="px-4 py-2 text-left">Duration</th>
                        <th className="px-4 py-2 text-left">Bonus Amount</th>
                        <th className="px-4 py-2 text-left">Winners</th>
                        <th className="px-4 py-2 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody id="seasonHistoryTable">
                      <tr>
                        <td colSpan="5" className="text-center py-4 text-gray-500">Loading...</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
      
      {/* Loot Modal */}
      {showLootModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-full max-w-2xl overflow-y-auto max-h-[90vh]">
            <h2 className="text-lg font-bold mb-4">
              {editingLoot ? 'Edit Loot' : 'Create New Loot'}
            </h2>
            <LootForm
              loot={editingLoot}
              onSuccess={handleLootSuccess}
              onCancel={() => {
                setShowLootModal(false);
                setEditingLoot(null);
              }}
            />
          </div>
        </div>
      )}

      {/* Block User Modal */}
      {showBlockModal && selectedUserForBlock && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-full max-w-md">
            <h2 className="text-lg font-bold mb-4">Block User</h2>
            <div className="mb-4">
              <p className="text-sm text-gray-600 mb-2">
                Are you sure you want to block <strong>{selectedUserForBlock.username}</strong>?
              </p>
              <p className="text-sm text-gray-600 mb-4">
                This will prevent the user from logging in until you unblock them.
              </p>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Reason (optional)
              </label>
              <textarea
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                placeholder="Enter reason for blocking..."
                value={blockReason}
                onChange={(e) => setBlockReason(e.target.value)}
                rows="3"
              />
            </div>
            <div className="flex gap-2">
              <Button 
                variant="danger" 
                onClick={() => handleBlockUser(selectedUserForBlock.id, blockReason)}
                className="flex-1"
              >
                Block User
              </Button>
              <Button 
                variant="secondary" 
                onClick={() => {
                  setShowBlockModal(false);
                  setSelectedUserForBlock(null);
                  setBlockReason('');
                }}
                className="flex-1"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
