import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Button from './Button';

const socket = io('/', { autoConnect: false });

export default function AdminChatBox() {
  const [participants, setParticipants] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    fetchParticipants();
    socket.connect();
    socket.emit('join-admin');
    
    socket.on('new-message', msg => {
      if (msg.sender_role === 'client' && msg.sender_id === selectedClient?.id) {
        setMessages(m => [...m, msg]);
      }
    });
    
    socket.on('connect', () => {
      console.log('Admin connected to chat server');
    });
    
    socket.on('connect_error', (error) => {
      console.error('Admin chat connection error:', error);
    });
    
    return () => {
      socket.off('new-message');
      socket.off('connect');
      socket.off('connect_error');
      socket.disconnect();
    };
    // eslint-disable-next-line
  }, [selectedClient]);

  useEffect(() => {
    if (selectedClient) fetchMessages(selectedClient.id);
  }, [selectedClient]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const fetchParticipants = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/chat/participants', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) setParticipants(data.data);
  };

  const markMessagesAsRead = async (messageIds) => {
    const token = localStorage.getItem('token');
    await fetch('/api/chat/messages/read', {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ message_ids: messageIds })
    });
  };

  const fetchMessages = async clientId => {
    setLoading(true);
    const token = localStorage.getItem('token');
    const res = await fetch(`/api/chat/conversation/${clientId}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    const data = await res.json();
    if (data.success) {
      setMessages(data.data);
      // Mark all unread messages as read
      const unreadIds = data.data.filter(m => !m.is_read && m.sender_role === 'client').map(m => m.id);
      if (unreadIds.length > 0) {
        markMessagesAsRead(unreadIds);
      }
    }
    setLoading(false);
  };

  const sendMessage = async e => {
    e.preventDefault();
    if (!input.trim() || !selectedClient) return;
    
    const msg = {
      message: input,
      sender_id: 'admin',
      toUserId: selectedClient.id,
      created_at: new Date().toISOString(),
      sender_name: 'Admin',
      sender_role: 'admin',
    };
    
    console.log('Admin sending message:', msg);
    setMessages(m => [...m, msg]);
    setInput('');
    socket.emit('send-message', msg);
    
    try {
      const res = await fetch('/api/chat/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, receiver_id: selectedClient.id }),
      });
      const data = await res.json();
      if (!data.success) {
        console.error('Failed to save admin message:', data.message);
      }
    } catch (error) {
      console.error('Error sending admin message:', error);
    }
  };

  return (
    <div className="flex gap-4">
      <div className="w-64 bg-white rounded shadow p-2 h-96 overflow-y-auto">
        <h3 className="font-semibold mb-2">Clients</h3>
        {participants.map(p => (
          <div
            key={p.id}
            className={`p-2 rounded cursor-pointer ${selectedClient?.id === p.id ? 'bg-primary-100 text-primary-900' : 'hover:bg-gray-100'}`}
            onClick={() => setSelectedClient(p)}
          >
            <div className="font-medium">{p.username}</div>
            <div className="text-xs text-gray-500">{p.email}</div>
          </div>
        ))}
      </div>
      <div className="flex-1 bg-white rounded shadow p-2 flex flex-col h-96">
        <h3 className="font-semibold mb-2">Chat</h3>
        {selectedClient ? (
          <>
            <div className="flex-1 overflow-y-auto bg-gray-50 rounded border border-gray-200 p-2">
              {loading ? (
                <div className="text-center text-gray-500">Loading...</div>
              ) : (
                <div className="flex flex-col gap-2">
                  {messages.map((msg, i) => (
                    <div key={i} className={`flex ${msg.sender_role === 'admin' ? 'justify-end' : 'justify-start'}`}>
                      <div className={`px-3 py-2 rounded-lg max-w-xs ${msg.sender_role === 'admin' ? 'bg-primary-100 text-primary-900' : 'bg-success-100 text-success-900'}`}>
                        <div className="text-xs font-semibold mb-1">{msg.sender_name} <span className="text-gray-400">({msg.sender_role})</span></div>
                        <div>{msg.message}</div>
                        <div className="text-xs text-gray-400 mt-1">{new Date(msg.created_at).toLocaleTimeString()}</div>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </div>
              )}
            </div>
            <form onSubmit={sendMessage} className="flex gap-2 mt-2">
              <input
                className="input flex-1"
                placeholder="Type your message..."
                value={input}
                onChange={e => setInput(e.target.value)}
              />
              <Button type="submit" variant="primary">Send</Button>
            </form>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-400">Select a client to chat</div>
        )}
      </div>
    </div>
  );
}
